function getBrowserLanguage() {
    const lang = navigator.language || navigator.userLanguage;
    return lang.split('-')[0];
}

function applyTranslations(lang = "en") {
    const selectedLang = translations[lang] ? lang : "en";
    const t = translations[selectedLang];
    
    document.querySelectorAll('[data-i18n]').forEach(el => {
        const key = el.getAttribute('data-i18n');
        if (t[key]) {
            // Use innerHTML to support HTML tags in translations (e.g. app_name)
            el.innerHTML = t[key];
        }
    });
    document.documentElement.lang = selectedLang;
}

window.i18n = {
    t: (key, lang = getBrowserLanguage()) => {
        const selectedLang = translations[lang] ? lang : "en";
        return translations[selectedLang][key] || translations["en"][key] || key;
    },
    apply: applyTranslations,
    getLang: getBrowserLanguage
};
